<?php namespace Config;// Create a new instance of our RouteCollection class.$routes = Services::routes();// Load the system's routing file first, so that the app and ENVIRONMENT// can override as needed.if (file_exists(SYSTEMPATH . 'Config/Routes.php')){	require SYSTEMPATH . 'Config/Routes.php';}/** * -------------------------------------------------------------------- * Router Setup * -------------------------------------------------------------------- */$routes->setDefaultNamespace('App\Controllers');$routes->setDefaultController('Home');$routes->setDefaultMethod('index');$routes->setTranslateURIDashes(false);$routes->set404Override('App\Controllers\F404::index');$routes->setAutoRoute(true);/** * -------------------------------------------------------------------- * Route Definitions * -------------------------------------------------------------------- */// We get a performance increase by specifying the default// route since we don't have to scan directories.$routes->add('/', 'Home::index');//$routes->add('/session', 'Home::session');$routes->add('/language/(:any)', 'Home::language/$1');$routes->add('/product', 'Product::index');$routes->add('/product/wordpress', 'Product::get/365/wordpress');$routes->add('/urun/wordpress', 'Product::get/365/wordpress');$routes->add('/product/corporate', 'Product::get/366/corporate');$routes->add('/urun/kurumsal', 'Product::get/366/corporate');$routes->add('/product/(:any)', 'Product::get/$1');$routes->add('/about-me', 'About::index');$routes->add('/hakkimda', 'About::index');$routes->add('/contact', 'Contact::index');$routes->add('/iletisim', 'Contact::index');$routes->add('/panel', 'panel\Home::index');$routes->add('/panel/home/index', 'panel\Home::index');$routes->add('/panel/home/(:any)', 'panel\Home::$1/$2/$3/$4');$routes->add('/panel/contact2/', 'panel\Contact2::index');$routes->add('/panel/content/(:any)', 'panel\Content::$1/$2/$3/$4');$routes->add('/panel/users', 'panel\Users::index');$routes->add('/panel/users/(:any)', 'panel\Users::$1/$2/$3/$4');$routes->add('/panel/login/', 'panel\Login::index');$routes->add('/panel/login/(:any)', 'panel\Login::$1/$2/$3/$4');/** * -------------------------------------------------------------------- * Additional Routing * -------------------------------------------------------------------- * * There will often be times that you need additional routing and you * need it to be able to override any defaults in this file. Environment * based routes is one such time. require() additional route files here * to make that happen. * * You will have access to the $routes object within that file without * needing to reload it. */if (file_exists(APPPATH . 'Config/' . ENVIRONMENT . '/Routes.php')){	require APPPATH . 'Config/' . ENVIRONMENT . '/Routes.php';}