<?phpfunction assignWhFiles($CI, $items) {    foreach($items as $k => $d) {        foreach($d->ct_files as $k_b => $d_b) {            $file_id = $d_b->value;            $file_db =                $CI->db->table("wh_files")->where(["id" => $file_id])                    ->get()                    ->getResult();            if ($CI->db->affectedRows() > 0) {                $d_b->fileName = $file_db[0]->file_name;            }        }    }    return $items;}function assignCats($CI, $items) {    foreach($items as $k => $d) {        foreach($d->ct_categories as $k_b => $d_b) {            $data_db =                $CI->db->table("ct_titles")->where(["parent_id" => $d_b->value])                    ->get()                    ->getResult();            if ($CI->db->affectedRows() > 0) {                $d_b->title = $data_db[0]->title;            }        }    }    return $items;}function loadpage($page = "", $data, $panel = false, $templatesB = false) {//    $CI =& get_instance();    $panelStr = "";    $templatesBStr = "";    if ($panel) {        $panelStr = "panel/";    }    if (!$templatesB) { $templatesBStr = "templates/"; } else { $templatesBStr = "templatesB/"; }//    echo " a:<pre>"; print_r($data); echo "</pre>"; die;    if ($page === "login") {        echo view($panelStr . $templatesBStr . "headerHtml.php");        echo view($panelStr . $page, $data);        echo view($panelStr . $templatesBStr . "footerHtml.php");    } else {        echo view($panelStr . $templatesBStr . "headerHtml.php", $data);        echo view($panelStr . $templatesBStr . "header.php", $data);        echo view($panelStr . $page, $data);        echo view($panelStr . $templatesBStr . "footer.php");        echo view($panelStr . $templatesBStr . "footerHtml.php");    }}function panelUserLoggedInCheck() {    if (!isset($_SESSION["puser_id"])) {/