MAYIS'; }    else if ($month == '6') { $month = 'HAZİRAN'; }    else if ($month == '7') { $month = 'TEMMUZ'; }    else if ($month == '8') { $month = 'AĞUSTOS'; }    else if ($month == '9') { $month = 'EYLÜL'; }    else if ($month == '10') { $month = 'EKİM'; }    else if ($month == '11') { $month = 'KASIM'; }    else if ($month == '12') { $month = 'ARALIK'; }    return $month;}function slugify($text, string $divider = '-'){    $text = str_replace([        'Ç', 'Ğ', 'İ', 'Ö', 'Ş', 'Ü'        ,'ç', 'ğ', 'ı', 'ö', 'ş', 'ü'    ], [        'c', 'g', 'i', 'o', 's', 'u'        ,'c', 'g', 'i', 'o', 's', 'u'    ], $text);    // replace non letter or digits by divider    $text = preg_replace('~[^\pL\d]+~u', $divider, $text);    // transliterate    $text = iconv('utf-8', 'us-ascii//TRANSLIT', $text);    // remove unwanted characters    $text = preg_replace('~[^-\w]+~', '', $text);    // trim    $text = trim($text, $divider);    // remove duplicate divider    $text = preg_replace('~-+~', $divider, $text);    // lowercase    $text = strtolower($text);    if (empty($text)) {        return 'n-a';    }    return $text;}